                <option value="kn">ಕನ್ನಡ (Kannada)</option>
            </select>
            <button class="close-btn" id="closeChat">✖</button>
        </div>
        <div class="chat-messages" id="messages"></div>
        <div class="chat-input">
            <input type="text" id="messageInput" placeholder="Ask your medical question..." />
            <button id="sendMessage">Send</button>
        </div>
    </div>

    <script src="chat.js"></script>
</body>
</html>

<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Health Reminder Assistant</title>
    <link rel="stylesheet" href="rem.css">
</head>
<body>
    <div class="container">
        <h1>Health Reminder Assistant</h1>
        <p>Set reminders for tablet intake and monthly check-ups</p>
        <p>ಟ್ಯಾಬ್ಲೆಟ್ ಸೇವನೆ ಮತ್ತು ಮಾಸಿಕ ತಪಾಸಣೆಗಾಗಿ ಜ್ಞಾಪನೆಗಳನ್ನು ಹೊಂದಿಸಿ</p>
        
        <label for="reminderTime">Tablet Reminder Time/ಟ್ಯಾಬ್ಲೆಟ್ ಜ್ಞಾಪನೆ ಸಮಯ:</label>
        <input type="time" id="reminderTime">

        <label for="checkupDate">Monthly Check-up Date/ಮಾಸಿಕ ತಪಾಸಣೆ ದಿನಾಂಕ:</label>
        <input type="date" id="checkupDate">

        <label for="languageSelect">Select Language/ಭಾಷೆಯನ್ನು ಆಯ್ಕೆಮಾಡಿ:</label>
        <select id="languageSelect">
            <option value="en-US">English</option>
            <option value="hi-IN">हिन्दी</option>
            <option value="kn-IN">ಕನ್ನಡ</option>
        </select>
        

        <button onclick="startReminders()">Start Reminders/ಜ್ಞಾಪನೆಗಳನ್ನು ಪ್ರಾರಂಭಿಸಿ</button>
    </div>
    
    <script src="rem.js"></script>
</body>
</html>

